riot.tag('btn-group', '<btn each="{button in buttons}" classname="{button}" text="{button}" type="button"></btn>', function(opts) {

    this.buttons = ['default', 'primary', 'success', 'info', 'warning', 'danger', 'link'];


});

riot.tag('btn-sizes', '<btn each="{button in buttons}" classname="{button.classname}" text="{button.text}" size="{button.size}"></btn>', function(opts) {

    this.buttons = [
        {classname: 'primary', text: 'Primary button text', size: 'lg'},
        {classname: 'success', text: 'Success button text'},
        {classname: 'info', text: 'Info button text', size: 'sm'},
        {classname: 'warning', text: 'Warning button text', size: 'xs'}
    ];


});

riot.tag('buttons-bad', '<btn each="{button in buttons}" classname="{button}" text="{button}"></btn> <btn-sizes></btn-sizes>', function(opts) {

    this.buttons = ['default', 'primary', 'success', 'info', 'warning', 'danger', 'link'];


});

riot.tag('buttons-with-wrapper', '<div class="bs-example"> <btn each="{button in buttons}" classname="{button}" text="{button}"></btn> </div> <div class="bs-example"> <btn-sizes></btn-sizes> </div>', function(opts) {

    this.buttons = ['default', 'primary', 'success', 'info', 'warning', 'danger', 'link'];


});

riot.tag('buttons-good', '<btn-sizes></btn-sizes> <btn each="{button in buttons}" classname="{button}" text="{button}"></btn>', function(opts) {

    this.buttons = ['default', 'primary', 'success', 'info', 'warning', 'danger', 'link'];


});

